package starPractice;

public class Star2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number=7;

		for ( int i=1;i<=number;i++)
		{

		for (int j=i;j<number;j++)

		System.out.print(" ");

		for (int k=1;k<(i*2);k++)

		System.out.print("*");
		System.out.println();

	}

}
}
